// Medicines page JavaScript

// Global variables
let allMedicines = [];
let filteredMedicines = [];
let currentPage = 1;
const itemsPerPage = 8;
let currentCategory = 'all';
let currentSort = 'name-asc';
let currentSearch = '';
// Default placeholder image
const PLACEHOLDER_IMAGE = 'data:image/jpeg;base64,';

// Wait for DOM to load
document.addEventListener('DOMContentLoaded', () => {
  // Load medicines data
  loadMedicines();
  
  // Setup event listeners
  setupEventListeners();
  
  // Check for search parameter in URL
  const urlParams = new URLSearchParams(window.location.search);
  const searchQuery = urlParams.get('search');
  
  if (searchQuery) {
    // Set search input value
    document.getElementById('search-input').value = searchQuery;
    
    // Set current search
    currentSearch = searchQuery;
  }
});

// Load medicines data
function loadMedicines() {
  // Show loader
  document.getElementById('medicines-grid').innerHTML = `
    <div class="loader-container" style="text-align: center; grid-column: 1 / -1; padding: 40px;">
      <span class="loader"></span>
    </div>
  `;
  
  // Fallback to local data immediately since we know Firebase is not set up
  loadMedicinesFromJSON();
}

