// Process medicines data
function processMedicines() {
  // Get unique categories
  const categories = [...new Set(allMedicines.map(medicine => medicine.category))];
  
  // Populate category filter
  populateCategoryFilter(categories);
  
  // Apply initial filtering and sorting
  applyFiltersAndSort();
}

// Populate category filter
function populateCategoryFilter(categories) {
  const categoryFilter = document.getElementById('category-filter');
  
  categories.forEach(category => {
    const option = document.createElement('option');
    option.value = category;
    option.textContent = category;
    categoryFilter.appendChild(option);
  });
}

// Setup event listeners
function setupEventListeners() {
  // Category filter
  document.getElementById('category-filter').addEventListener('change', function() {
    currentCategory = this.value;
    currentPage = 1;
    applyFiltersAndSort();
  });
  
  // Sort selector
  document.getElementById('sort-by').addEventListener('change', function() {
    currentSort = this.value;
    applyFiltersAndSort();
  });
  
  // Reset filters
  document.getElementById('reset-filters').addEventListener('click', function() {
    document.getElementById('category-filter').value = 'all';
    document.getElementById('sort-by').value = 'name-asc';
    document.getElementById('search-input').value = '';
    
    currentCategory = 'all';
    currentSort = 'name-asc';
    currentSearch = '';
    currentPage = 1;
    
    applyFiltersAndSort();
  });
  
  // Search form
  document.getElementById('search-form').addEventListener('submit', function(e) {
    e.preventDefault();
    
    currentSearch = document.getElementById('search-input').value.trim().toLowerCase();
    currentPage = 1;
    
    applyFiltersAndSort();
  });
  
  // Medicine modal
  setupMedicineModal();
}

// Apply filters and sort
function applyFiltersAndSort() {
  // Filter medicines
  filteredMedicines = allMedicines.filter(medicine => {
    // Category filter
    const categoryMatch = currentCategory === 'all' || medicine.category === currentCategory;
    
    // Search filter
    let searchMatch = true;
    
    if (currentSearch) {
      const searchTerms = currentSearch.split(' ');
      
      searchMatch = searchTerms.every(term => {
        return (
          medicine.name.toLowerCase().includes(term) ||
          medicine.description.toLowerCase().includes(term) ||
          medicine.category.toLowerCase().includes(term) ||
          (medicine.tags && medicine.tags.some(tag => tag.toLowerCase().includes(term)))
        );
      });
    }
    
    return categoryMatch && searchMatch;
  });
  
  // Sort medicines
  sortMedicines();
  
  // Update results count
  updateResultsCount();
  
  // Display medicines
  displayMedicines();
  
  // Setup pagination
  setupPagination();
}

// Sort medicines
function sortMedicines() {
  switch (currentSort) {
    case 'name-asc':
      filteredMedicines.sort((a, b) => a.name.localeCompare(b.name));
      break;
    case 'name-desc':
      filteredMedicines.sort((a, b) => b.name.localeCompare(a.name));
      break;
    case 'price-asc':
      filteredMedicines.sort((a, b) => a.price - b.price);
      break;
    case 'price-desc':
      filteredMedicines.sort((a, b) => b.price - a.price);
      break;
    case 'popularity':
      filteredMedicines.sort((a, b) => (b.popularity || 0) - (a.popularity || 0));
      break;
  }
}

// Update results count
function updateResultsCount() {
  const resultsCount = document.getElementById('results-count');
  
  if (filteredMedicines.length === 0) {
    resultsCount.textContent = 'No medicines found.';
  } else if (filteredMedicines.length === 1) {
    resultsCount.textContent = '1 medicine found.';
  } else {
    resultsCount.textContent = `${filteredMedicines.length} medicines found.`;
  }
}

// Display medicines
function displayMedicines() {
  const medicinesGrid = document.getElementById('medicines-grid');
  
  if (filteredMedicines.length === 0) {
    medicinesGrid.innerHTML = `
      <div class="no-results" style="grid-column: 1 / -1; text-align: center; padding: 40px;">
        <i class="fas fa-search" style="font-size: 3rem; color: var(--border-color); margin-bottom: 15px;"></i>
        <p>No medicines found matching your search criteria.</p>
        <button class="btn btn-outline" onclick="document.getElementById('reset-filters').click()">Reset Filters</button>
      </div>
    `;
    
    // Hide pagination
    document.getElementById('pagination').style.display = 'none';
    
    return;
  }
  
  // Calculate slice for current page
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const medicinesOnPage = filteredMedicines.slice(startIndex, endIndex);
  
  // Generate HTML for medicines
  let html = '';
  
  medicinesOnPage.forEach(medicine => {
    html += `
      <div class="card medicine-card" data-id="${medicine.id}">
        <img src="${medicine.image || PLACEHOLDER_IMAGE}" alt="${medicine.name}" class="card-image">
        <div class="card-content">
          <h3 class="card-title">${medicine.name}</h3>
          <p class="card-text">${medicine.description.substring(0, 100)}...</p>
          <p class="card-price">$${medicine.price.toFixed(2)}</p>
          
          ${medicine.prescription_required ? `
            <p class="prescription-required" style="color: #e74c3c; font-size: 0.8rem; margin-top: 5px;">
              <i class="fas fa-prescription"></i> Prescription Required
            </p>
          ` : ''}
          
          <div class="card-actions">
            <button class="btn btn-outline view-medicine-btn" data-id="${medicine.id}">
              View Details
            </button>
            
            ${!medicine.prescription_required ? `
              <button class="btn btn-primary add-to-cart-btn" data-id="${medicine.id}">
                <i class="fas fa-shopping-cart"></i> Add
              </button>
            ` : ''}
          </div>
        </div>
      </div>
    `;
  });
  
  medicinesGrid.innerHTML = html;
  
  // Show pagination
  document.getElementById('pagination').style.display = 'flex';
  
  // Add event listeners to buttons
  addCardButtonListeners();
}

// Add event listeners to medicine card buttons
function addCardButtonListeners() {
  // View details buttons
  document.querySelectorAll('.view-medicine-btn').forEach(button => {
    button.addEventListener('click', function() {
      const medicineId = this.getAttribute('data-id');
      openMedicineModal(medicineId);
    });
  });
  
  // Add to cart buttons
  document.querySelectorAll('.add-to-cart-btn').forEach(button => {
    button.addEventListener('click', function() {
      const medicineId = this.getAttribute('data-id');
      const medicine = allMedicines.find(med => med.id === medicineId);
      
      if (medicine) {
        addToCart(medicine);
      }
    });
  });
  
  // Make entire card clickable
  document.querySelectorAll('.medicine-card').forEach(card => {
    card.addEventListener('click', function(e) {
      // Only trigger if not clicked on buttons
      if (!e.target.closest('button')) {
        const medicineId = this.getAttribute('data-id');
        openMedicineModal(medicineId);
      }
    });
  });
}

// Setup pagination
function setupPagination() {
  const paginationContainer = document.getElementById('pagination');
  const totalPages = Math.ceil(filteredMedicines.length / itemsPerPage);
  
  if (totalPages <= 1) {
    paginationContainer.style.display = 'none';
    return;
  }
  
  let html = '';
  
  // Previous button
  html += `
    <button class="pagination-btn prev-btn ${currentPage === 1 ? 'disabled' : ''}" 
      ${currentPage === 1 ? 'disabled' : ''}>
      <i class="fas fa-chevron-left"></i>
    </button>
  `;
  
  // Page numbers
  const maxVisiblePages = 5;
  let startPage = Math.max(1, currentPage - Math.floor(maxVisiblePages / 2));
  let endPage = Math.min(totalPages, startPage + maxVisiblePages - 1);
  
  // Adjust startPage if endPage is maxed out
  if (endPage === totalPages) {
    startPage = Math.max(1, endPage - maxVisiblePages + 1);
  }
  
  // First page
  if (startPage > 1) {
    html += `<button class="pagination-btn page-btn" data-page="1">1</button>`;
    
    if (startPage > 2) {
      html += `<span class="pagination-ellipsis">...</span>`;
    }
  }
  
  // Page numbers
  for (let i = startPage; i <= endPage; i++) {
    html += `
      <button class="pagination-btn page-btn ${i === currentPage ? 'active' : ''}" 
        data-page="${i}">
        ${i}
      </button>
    `;
  }
  
  // Last page
  if (endPage < totalPages) {
    if (endPage < totalPages - 1) {
      html += `<span class="pagination-ellipsis">...</span>`;
    }
    
    html += `<button class="pagination-btn page-btn" data-page="${totalPages}">${totalPages}</button>`;
  }
  
  // Next button
  html += `
    <button class="pagination-btn next-btn ${currentPage === totalPages ? 'disabled' : ''}" 
      ${currentPage === totalPages ? 'disabled' : ''}>
      <i class="fas fa-chevron-right"></i>
    </button>
  `;
  
  paginationContainer.innerHTML = html;
  
  // Add event listeners to pagination buttons
  addPaginationEventListeners();
}

// Add event listeners to pagination buttons
function addPaginationEventListeners() {
  // Page buttons
  document.querySelectorAll('.pagination-btn.page-btn').forEach(button => {
    button.addEventListener('click', function() {
      currentPage = parseInt(this.getAttribute('data-page'));
      displayMedicines();
      setupPagination();
      
      // Scroll to top of medicines grid
      document.getElementById('medicines-grid').scrollIntoView({ behavior: 'smooth' });
    });
  });
  
  // Previous button
  const prevBtn = document.querySelector('.pagination-btn.prev-btn');
  if (prevBtn && !prevBtn.disabled) {
    prevBtn.addEventListener('click', function() {
      if (currentPage > 1) {
        currentPage--;
        displayMedicines();
        setupPagination();
        
        // Scroll to top of medicines grid
        document.getElementById('medicines-grid').scrollIntoView({ behavior: 'smooth' });
      }
    });
  }
  
  // Next button
  const nextBtn = document.querySelector('.pagination-btn.next-btn');
  if (nextBtn && !nextBtn.disabled) {
    nextBtn.addEventListener('click', function() {
      const totalPages = Math.ceil(filteredMedicines.length / itemsPerPage);
      
      if (currentPage < totalPages) {
        currentPage++;
        displayMedicines();
        setupPagination();
        
        // Scroll to top of medicines grid
        document.getElementById('medicines-grid').scrollIntoView({ behavior: 'smooth' });
      }
    });
  }
}

// Setup medicine modal
function setupMedicineModal() {
  const modal = document.getElementById('medicine-modal');
  const closeModalBtn = document.getElementById('close-modal');
  const modalCloseBtn = document.getElementById('modal-close-btn');
  
  // Close modal on button click
  closeModalBtn.addEventListener('click', function() {
    closeModal();
  });
  
  modalCloseBtn.addEventListener('click', function() {
    closeModal();
  });
  
  // Close modal when clicking outside
  window.addEventListener('click', function(e) {
    if (e.target === modal) {
      closeModal();
    }
  });
  
  // Close modal with Escape key
  document.addEventListener('keydown', function(e) {
    if (e.key === 'Escape' && modal.style.display === 'flex') {
      closeModal();
    }
  });
  
  // Add to cart button in modal
  document.getElementById('modal-add-to-cart-btn').addEventListener('click', function() {
    const medicineId = this.getAttribute('data-medicine-id');
    const medicine = allMedicines.find(med => med.id === medicineId);
    
    if (medicine) {
      addToCart(medicine);
      closeModal();
    }
  });
  
  // Function to close modal
  function closeModal() {
    modal.style.display = 'none';
    document.body.style.overflow = 'auto';
  }
}

// Open medicine modal
function openMedicineModal(medicineId) {
  const medicine = allMedicines.find(med => med.id === medicineId);
  
  if (!medicine) return;
  
  // Set modal content
  document.getElementById('modal-medicine-name').textContent = medicine.name;
  document.getElementById('modal-medicine-description').textContent = medicine.description;
  document.getElementById('modal-medicine-dosage').textContent = medicine.dosage;
  document.getElementById('modal-medicine-category').textContent = medicine.category;
  document.getElementById('modal-medicine-price').textContent = `$${medicine.price.toFixed(2)}`;
  
  // Set medicine image
  const medicineImage = document.getElementById('modal-medicine-image');
  medicineImage.src = medicine.image || PLACEHOLDER_IMAGE;
  medicineImage.alt = medicine.name;
  
  // Show/hide prescription required
  const prescriptionContainer = document.getElementById('modal-medicine-prescription-container');
  if (medicine.prescription_required) {
    prescriptionContainer.style.display = 'block';
  } else {
    prescriptionContainer.style.display = 'none';
  }
  
  // Set medicine tags
  const tagsContainer = document.getElementById('modal-medicine-tags');
  
  if (medicine.tags && medicine.tags.length > 0) {
    let tagsHTML = '';
    
    medicine.tags.forEach(tag => {
      tagsHTML += `<span class="medicine-tag">${tag}</span>`;
    });
    
    tagsContainer.innerHTML = tagsHTML;
    tagsContainer.style.display = 'flex';
  } else {
    tagsContainer.style.display = 'none';
  }
  
  // Enable/disable add to cart button
  const addToCartBtn = document.getElementById('modal-add-to-cart-btn');
  
  if (medicine.prescription_required) {
    addToCartBtn.disabled = true;
    addToCartBtn.title = 'Prescription required for this medicine';
    addToCartBtn.classList.add('disabled');
  } else {
    addToCartBtn.disabled = false;
    addToCartBtn.title = '';
    addToCartBtn.classList.remove('disabled');
    addToCartBtn.setAttribute('data-medicine-id', medicine.id);
  }
  
  // Show modal
  const modal = document.getElementById('medicine-modal');
  modal.style.display = 'flex';
  document.body.style.overflow = 'hidden';
} 